import { SchemaTypeOpts } from "mongoose";
/**
 * Tell Mongoose whether to define an index for the property.
 * ### Example
 *
 * ```typescript
 * @Model()
 * export class EventModel {
 *   @Indexed()
 *   field: string;
 * }
 * ```
 *
 * @returns {Function}
 * @decorator
 * @mongoose
 * @property
 */
export declare function Indexed(index?: SchemaTypeOpts.IndexOpts | boolean | string): Function;
